import Vue from "vue";
import VueRouter from "vue-router";
import HomeView from "../views/home/home/HomeView.vue";
import Layout from "../views/Layout.vue";

Vue.use(VueRouter);

const routes = [
  // Login
  {
    path: "/login",
    name: "Login",
    component: () => import("../../src/views/login/Login.vue"),
  },
  // 
  {
    path: "/",
    name: "Layout",
    redirect: "/home",
    component: Layout,
    children: [
      // 主页
      {
        path: "home",
        name: "Home",
        component: () => import("../../src/views/home/HomeView.vue"),
      },
      // User
      {
        path: "userList",
        name: "UserList",
        component: () => import("../../src/views/user/User.vue"),
      },
      {
        path: "addUser",
        name: "AddUser",
        component: () => import("../../src/views/user/AddUser.vue"),
      },
      {
        path: "editUser",
        name: "EditUser",
        component: () => import("../../src/views/user/EditUser.vue"),
      },
      // Admin
      {
        path: "adminList",
        name: "AdminList",
        component: () => import("../../src/views/admin/List.vue"),
      },
      {
        path: "addAdmin",
        name: "AddAdmin",
        component: () => import("../../src/views/admin/Add.vue"),
      },
      {
        path: "editAdmin",
        name: "EditAdmin",
        component: () => import("../../src/views/admin/Edit.vue"),
      },
    ],
  },
];

const router = new VueRouter({
  mode: "history",
  base: process.env.BASE_URL,
  routes,
});

export default router;
